import React from 'react';

class Experience extends React.Component {
	render() {
		if ( this.props.data ) {
			var education = this.props.data.education.map(function(education){
				return <div key={education.school}>
					<p class="date"><span class="font-italic">{education.graduated}</span></p>
					<p className="degree">{education.degree}</p>
					<p>{education.school}</p>
					<p key={education.gpa}>GPA: {education.gpa}</p>
					<p>{education.description}</p>
				</div>
			})
			var work = this.props.data.work.map(function(work){
				return <div key={work.company}><p><strong>{work.company}</strong></p>
				<p className="info">{work.title}<span>&bull;</span> <em className="date">{work.years}</em></p>
				<p>{work.description}</p>
				</div>
			})
		}
		return (
			<section id="Experience" className="segment space-small">
			<div className="container">
			<div className="row">
			<div className="col-md-12">
			<h4><strong>Experience</strong></h4>
			</div>
			</div>
			<div className="row">
			<div className="col-md-12">
			{education}
			</div>
			</div>
			<div className="row">
			<div className="col-md-12">
			{work}
			</div>
			</div>
			</div>
			</section>
			);
		}
	}
	export default Experience;
